import React from "react";
import {useState,useEffect} from 'react';

function TodoList() {
    const [todos,setTodos] = useState([]);
    const [postTodo,setPostTodo] = useState({})
    const [formStatus,setFormStatus] = useState({
        button: false,
        inTodo: false,
        inProgress: false,
        inComplete: false
    })

    console.log(document.getElementById('inTodoList'))

    const checkForCheck = (e) => {
        e.preventDefault();
        const todoListCheck = document.getElementById('inTodoList');
        console.log(todoListCheck.checked)
        if (todoListCheck.checked){
            console.log(formStatus)
            setFormStatus({inProgress:true})
            setFormStatus({inComplete:true})
        }
    }

    const handleCheck = (e) => {
        const todoListCheck = document.getElementById('inTodoList');
        console.log(todoListCheck.checked)
        if (todoListCheck.checked){
            console.log(formStatus)
            setFormStatus({inProgress:true})
            setFormStatus({inComplete:true})
        }; 
    }
    
    return (
        <div className="App">
            <form onSubmit={checkForCheck}>
                <input type='text' placeholder='Todo Name' />
                <input type='text' placeholder='Todo Description'/>
                <button id="postTodo" type='submit' disabled={formStatus.button} >
                    Add Todo!
                </button>
                <br/>
                <input type='checkbox' value='inTodoList' id='inTodoList' name='inTodoList' onClick={handleCheck}/>
                <label for='inTodoList'> Todo </label>
                <input type='checkbox' value='inProgressList' id='inProgressList' name='inProgressList' disabled={formStatus.inProgress} />
                <label for ='inProgressList'> In Progress </label>
                <input type='checkbox' value='inCompleteList' id='inCompleteList' name='inCompleteList' disabled={formStatus.inComplete} />
                <label for ='inCompleteList'> Complete </label>
                <br/>
            </form>
        </div>
    );
}
    
export default TodoList;
