import React from "react";
import {useState,useEffect} from 'react';
import axios from 'axios'
import TodoCard from "./todoCard";
import '../App.css';


function TodoList() {
    const [todos,setTodos] = useState([]);
    const [postTodo,setPostTodo] = useState({});
    const [data,setData] = useState({ title: '', description: '', list:''});
    const [update,setUpdate] = useState(false);
    const [id,setId] = useState('');
    const [formStatus,setFormStatus] = useState({
        button: true,
        inTodo: false,
        inProgress: false,
        inComplete: false
    });

    const todoListCheck = document.getElementById('inTodoList');
    const inProgressListCheck = document.getElementById('inProgressList');
    const inCompleteListCheck = document.getElementById('inCompleteList');

    const handleSubmit = (e) => {
        e.preventDefault();
        let dueDate = document.getElementById('dueDate')
        console.log(dueDate.value)
        axios({
            method: 'post',
            url:'http://localhost:8000/api/todo',
            data:{
                title: data.title,
                description: data.description,
                list: data.list
            }
        });
        axios
                .get("http://localhost:8000/api/todo")
                .then((res) => {
                    console.log(res.data);
                    setTodos(res.data);
                })
                .catch((err) => {
                    console.log(err.message);
                });
        if (update) {
            setUpdate(false)
        }else{
            setUpdate(true)
        } 
        setData({
            title:'',description:'',list:''
        });
        inProgressListCheck.checked=false;
        inCompleteListCheck.checked=false;
        todoListCheck.checked=false;
    };

    useEffect(
        function () {
            axios
                .get("http://localhost:8000/api/todo")
                .then((res) => {
                    console.log(res.data);
                    setTodos(res.data);
                })
                .catch((err) => {
                    console.log(err.message);
                });
        },
        [update]
    );

    let todo = [];
    let inProgress = [];
    let complete = [];
    
    for (let i = 0; i < todos.length; i++) {
        if (todos[i].list === 'Todo'){
            todo = [...todo,todos[i]]
        };

        if (todos[i].list === 'InProgress'){
            inProgress = [...inProgress,todos[i]]
        };
        if (todos[i].list === 'Complete'){
            complete = [...complete,todos[i]]
        };
    };

    function handleChange(e) {
        setData((data) => ({...data, [e.target.name]: e.target.value}));
    };

    const handleTodoListCheck = (e) => {
        inProgressListCheck.checked=false;
        inCompleteListCheck.checked=false;
        setFormStatus(existingValues => ({
            ...existingValues,
            button:false
        }))

        setData(existingValues => ({
            ...existingValues,
            list:'Todo'
        }));

        if(!todoListCheck.checked){
            setFormStatus(existingValues => ({
                ...existingValues,
                button:true
            })); 
        };
    };

    const handleInProgressListCheck = (e) => {
        todoListCheck.checked=false
        inCompleteListCheck.checked=false
        setFormStatus(existingValues => ({
            ...existingValues,
            button:false
        }))

        setData(existingValues => ({
            ...existingValues,
            list:'InProgress'
        }));

        if(!inProgressListCheck.checked){
            setFormStatus(existingValues => ({
                ...existingValues,
                button:true
            })); 
        };
    };

    const handleInCompleteListCheck = (e) => {
        inProgressListCheck.checked=false
        todoListCheck.checked=false
        setFormStatus(existingValues => ({
            ...existingValues,
            button:false
        }))

        setData(existingValues => ({
            ...existingValues,
            list:'Complete'
        }));

        if(!inCompleteListCheck.checked){
            setFormStatus(existingValues => ({
                ...existingValues,
                button:true
            })); 
        };
    };

    function handleDelete(e) {
        console.log('BELOW')
        console.log(todos)
        axios.delete(`http://localhost:8000/api/todo/${e.target.name}`);
        setTodos((data) => {
            return data.filter((todo) => todo._id !== e.target.name);
        });
    };

    function handleMove(e) {
        let mover = e.target.value
        let moverId = e.target.name

        if (mover==='ignore'){
            return
        }
        
        for (let i = 0; i < todos.length; i++) {
            if (todos[i]._id === moverId){
                todos[i].list=mover
    
                const newTodo = {
                    title: todos[i].title,
                    description: todos[i].description,
                    list: mover
                };
                axios.put(`http://localhost:8000/api/todo/${moverId}`,newTodo)
                console.log(newTodo)
                if (update) {
                    setUpdate(false)
                }else{
                    setUpdate(true)
                } 
            }  
        }
    };

    return (
        <div className='App'>
            <div className="flex-container">
                <div className='todoLists'>
                    <h1 style={{textDecoration: 'underline', textDecorationColor: '#fbff0e'}}>Todo</h1>
                    <ul>
                        {todo.map((data) => (
                            <TodoCard
                                data={data}
                                handleDelete={handleDelete}
                                handleMove={handleMove}
                            />
                        ))}                        
                    </ul>
                </div>
                <div className='inProgressLists'>
                    <h1 style={{textDecoration: 'underline', textDecorationColor: '#ff452c'}}>In Progress</h1>
                    <ul>
                        {inProgress.map((data) => (
                            <TodoCard
                                data={data}
                                handleDelete={handleDelete}
                                handleMove={handleMove}
                            />
                        ))}                        
                    </ul>
                </div>
                <div className='inCompleteLists'>
                    <h1 style={{textDecoration: 'underline', textDecorationColor: '#a9ff31'}}>Complete</h1>
                   <ul>
                        {complete.map((data) => (
                            <TodoCard
                                data={data}
                                handleDelete={handleDelete}
                                handleMove={handleMove}
                            />
                        ))}                        
                    </ul> 
                </div>
            </div>
            <br/>
            <form className='form' onSubmit={handleSubmit}>
                <h3 className="formElements" >Add Todo: </h3>
                <input className="formElements" type='text' placeholder='Todo Name' name='title' value={data.title} onChange={handleChange} />
                <input className="formElements" type='text' placeholder='Todo Description' name='description' value={data.description} onChange={handleChange} />                
                <input className="formElements" type='date' placeholder='Due Date' name='date' id="dueDate"/>
                <input type='checkbox' value='inTodoList' id='inTodoList' name='inTodoList' onClick={handleTodoListCheck} />
                <label for='inTodoList'> Todo </label>
                <input type='checkbox' value='inProgressList' id='inProgressList' name='inProgressList'  onClick={handleInProgressListCheck} />
                <label for ='inProgressList'> In Progress </label>
                <input type='checkbox' value='inCompleteList' id='inCompleteList' name='inCompleteList'  onClick={handleInCompleteListCheck} />
                <label for ='inCompleteList'> Complete </label>
                <button className="formElements" id="postTodo" type='submit' disabled={formStatus.button} >
                    Add Todo!
                </button>
            </form>
        </div>
    );
}
    
export default TodoList;
