import React from "react";
import {useState,useEffect} from 'react';
import axios from 'axios'
import TodoCard from "./todoCard";

function TodoList() {
    const [todos,setTodos] = useState([]);
    const [todo,setTodo] = useState([]);
    const [inProgress,setInProgress] = useState([]);
    const [complete,setComplete] = useState([]);
    const [postTodo,setPostTodo] = useState({});
    const [data,setData] = useState({ title: '', description: '', list:''});
    const [update,setUpdate] = useState(false);
    const [id,setId] = useState('');
    const [formStatus,setFormStatus] = useState({
        button: true,
        inTodo: false,
        inProgress: false,
        inComplete: false
    });

    const todoListCheck = document.getElementById('inTodoList');
    const inProgressListCheck = document.getElementById('inProgressList');
    const inCompleteListCheck = document.getElementById('inCompleteList');

    useEffect(
        function () {
            axios
                .get("http://localhost:8000/api/todo")
                .then((res) => {
                    console.log(res.data);
                    setTodos(res.data);
                })
                .catch((err) => {
                    console.log(err.message);
                });
        },
        [update]
    );

    let tempTodos = [];
    let tempInProgress = [];
    let tempComplete = [];
    
    for (let i = 0; i < todos.length; i++) {
        if (todos[i].list === 'Todo'){
            tempTodos = [...tempTodos,todos[i]]
        };

        if (todos[i].list === 'InProgress'){
            tempInProgress = [...tempInProgress,todos[i]]
        };
        if (todos[i].list === 'Complete'){
            tempComplete = [...tempComplete,todos[i]]
        };
    };

 //   setTodo(tempTodos);
 //   setInProgress(tempInProgress);
 //   setComplete(tempComplete);

    console.log('test')
    console.log(tempTodos)
    console.log(tempInProgress)
    console.log(tempComplete)

    function handleChange(e) {
        setData((data) => ({...data, [e.target.name]: e.target.value}));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios({
            method: 'post',
            url:'http://localhost:8000/api/todo',
            data:{
                title: data.title,
                description: data.description,
                list: data.list
            }
        });
        setData({
            title:'',description:'',list:''
        });
        inProgressListCheck.checked=false;
        inCompleteListCheck.checked=false;
        todoListCheck.checked=false;
    };

    const handleTodoListCheck = (e) => {
        inProgressListCheck.checked=false;
        inCompleteListCheck.checked=false;
        setFormStatus(existingValues => ({
            ...existingValues,
            button:false
        }))

        setData(existingValues => ({
            ...existingValues,
            list:'Todo'
        }));

        if(!todoListCheck.checked){
            setFormStatus(existingValues => ({
                ...existingValues,
                button:true
            })); 
        };
    };

    const handleInProgressListCheck = (e) => {
        todoListCheck.checked=false
        inCompleteListCheck.checked=false
        setFormStatus(existingValues => ({
            ...existingValues,
            button:false
        }))

        setData(existingValues => ({
            ...existingValues,
            list:'InProgress'
        }));

        if(!inProgressListCheck.checked){
            setFormStatus(existingValues => ({
                ...existingValues,
                button:true
            })); 
        };
    };

    const handleInCompleteListCheck = (e) => {
        inProgressListCheck.checked=false
        todoListCheck.checked=false
        setFormStatus(existingValues => ({
            ...existingValues,
            button:false
        }))

        setData(existingValues => ({
            ...existingValues,
            list:'Complete'
        }));

        if(!inCompleteListCheck.checked){
            setFormStatus(existingValues => ({
                ...existingValues,
                button:true
            })); 
        };
    };

    function handleDelete(e) {
        axios.delete(`http://localhost:8000/api/todo/${e.target.name}`);
        setTodos((data) => {
            return data.filter((todo) => todo._id !== e.target.name);
        });
    };
   
    return (
        <div classname=''>
            <div>
                <h1> Todos </h1>
                <ul>
                    {todos.map((data) => (
                        <TodoCard
                            data={data}
                            handleDelete={handleDelete}
                        />
                    ))}                        
                </ul>
            </div>
            <br/>
            <form onSubmit={handleSubmit}>
                <input type='text' placeholder='Todo Name' name='title' value={data.title} onChange={handleChange} />
                <input type='text' placeholder='Todo Description' name='description' value={data.description} onChange={handleChange} />                
                <input type='checkbox' value='inTodoList' id='inTodoList' name='inTodoList' onClick={handleTodoListCheck} />
                <label for='inTodoList'> Todo </label>
                <input type='checkbox' value='inProgressList' id='inProgressList' name='inProgressList'  onClick={handleInProgressListCheck} />
                <label for ='inProgressList'> In Progress </label>
                <input type='checkbox' value='inCompleteList' id='inCompleteList' name='inCompleteList'  onClick={handleInCompleteListCheck} />
                <label for ='inCompleteList'> Complete </label>
                <button id="postTodo" type='submit' disabled={formStatus.button} >
                    Add Todo!
                </button>
                {console.log(data)}
                <br/>
            </form>
        </div>
    );
}
    
export default TodoList;
