import React from "react";
import {useState,useEffect} from 'react';

function TodoList() {
    const [todos,setTodos] = useState([]);
    const [postTodo,setPostTodo] = useState({})

    console.log(document.getElementById('inTodoList'))

    const checkForCheck = () => {
        const todoListCheck = document.getElementById('inTodoList');
        if (todoListCheck.checked){
            console.log('its checkcd!')
        }
    }
    
    return (
        <div className="App">
            <form htmlFor="postTodo" onSubmit={checkForCheck}>
                <input type='text' placeholder='Todo Name' />
                <input type='text' placeholder='Todo Description'/>
                <button id="postTodo" type='button' disabled={false} >
                    Add Todo!
                </button>
                <br/>
                <input type='checkbox' value='inTodoList' id='inTodoList' name='inTodoList'/>
                <label for='inTodoList'> Todo </label>
                <input type='checkbox' value='inProgressList' id='inProgressList' name='inProgressList'/>
                <label for ='inProgressList'> In Progress </label>
                <input type='checkbox' value='inCompleteList' id='inCompleteList' name='inCompleteList'/>
                <label for ='inCompleteList'> Complete </label>
                <br/>
            </form>
        </div>
    );
}
    
export default TodoList;
