import '../App.css'; 
function TodoCard({ data, handleDelete, handleMove }) {

    const { _id, title, description,list,dueDate } = data;

    //ondoubleclick - 1) hide text 2) show input box 3) make input box == current title 4) allow users to edit 5) enter changes title 6)axios put request
    function handleEdit() {
        var textInput = document.getElementById('popup');
        textInput.classList.toggle('hide');
        textInput.classList.toggle('show');

        var textTitle = document.getElementById('title');
        textTitle.classList.toggle('hide');
        textTitle.classList.toggle('show');
    };

    function handleTitleChange(e) {
        console.log(e)
    }

    return (
        <div key={_id}>
            <div className="title-description">
                <h2 className='show' onClick={handleEdit} id='title'>{title}</h2>
                <input type='text' name='popup' id='popup' className='hide' onChange={handleTitleChange}></input>
                <p>{description}</p>
                <p>{dueDate}</p>
            </div>
            <div className="button-container">
                <button name={_id} className="button" onClick={handleDelete}>
                    Delete
                </button>
                <button name={_id} className='button' onClick={handleEdit}>
                    Edit
                </button>
            </div>
            <div>
                <select name={_id} class='browser-default' className='dropdown' id={_id} onChange={handleMove}>
                    <option value='ignore'>Move</option>
                    <option value='Todo'>Todo</option>
                    <option value='InProgress'>In Progress</option>
                    <option value='Complete'>Complete</option>
                </select>
            </div>
        </div>
    );
}

export default TodoCard;
