function TodoCard({ data, handleDelete }) {

    const { _id, title, description,list } = data;

    const handleMove = () => {
        let mover = document.getElementById('move');
        console.log(mover.value)
    }
    

    return (
        <div key={_id}>
            <div className="title-description">
                <h3>{title}</h3>
                <p>{description}</p>
            </div>

            <div className="button-container">
                <button name={_id} className="button">
                    Edit
                </button>

                <button name={_id} className="button" onClick={handleDelete}>
                    Delete
                </button>

                <select name={_id} className='button' id='move' onClick={handleMove}>
                    <option>{data.list}</option>
                    <option value='Todo'>Todo</option>
                    <option value='In Progress'>In Progress</option>
                    <option value='Complete'>Complete</option>
                </select>
            </div>
        </div>
    );
}

export default TodoCard;
