import React from "react";
import {useState,useEffect} from 'react';

function TodoList() {
    const [todos,setTodos] = useState([]);
    const [postTodo,setPostTodo] = useState({})
    const [formStatus,setFormStatus] = useState({
        button: true,
        inTodo: false,
        inProgress: false,
        inComplete: false
    })

    const checkForCheck = (e) => {
        e.preventDefault();
        const todoListCheck = document.getElementById('inTodoList');
        console.log(todoListCheck.checked)
        if (todoListCheck.checked){
            console.log(formStatus)
            setFormStatus({inProgress:true})
            setFormStatus({inComplete:true})
        }
    };

    const handleCheck = (e) => {
        const todoListCheck = document.getElementById('inTodoList');
        const inProgressListCheck = document.getElementById('inProgressList');
        const inCompleteListCheck = document.getElementById('inCompleteList')

        setFormStatus(existingValues => ({
            ...existingValues,
            button:true,
            inTodo: false,
            inProgress: false,
            inComplete: false
        }));

        if (todoListCheck.checked){
            console.log(formStatus)
            setFormStatus(existingValues => ({
                ...existingValues,
                button:false,
                inProgress: true,
                inComplete: true
            }))
        };

        if (inProgressListCheck.checked){
            console.log(formStatus)
            setFormStatus(existingValues => ({
                ...existingValues,
                button:false,
                inTodo: true,
                inComplete: true
            }))
        };

        if (inCompleteListCheck.checked){
            console.log(formStatus)
            setFormStatus(existingValues => ({
                ...existingValues,
                button:false,
                inTodo: true,
                inProgress: true
            }))
        }; 
    };
    
    return (
        <div className="App">
            <form onSubmit={checkForCheck}>
                <input type='text' placeholder='Todo Name' />
                <input type='text' placeholder='Todo Description'/>
                <button id="postTodo" type='submit' disabled={formStatus.button} >
                    Add Todo!
                </button>
                <br/>
                <input type='checkbox' value='inTodoList' id='inTodoList' name='inTodoList' disabled={formStatus.inTodo} onClick={handleCheck} />
                <label for='inTodoList'> Todo </label>
                <input type='checkbox' value='inProgressList' id='inProgressList' name='inProgressList' disabled={formStatus.inProgress} onClick={handleCheck} />
                <label for ='inProgressList'> In Progress </label>
                <input type='checkbox' value='inCompleteList' id='inCompleteList' name='inCompleteList' disabled={formStatus.inComplete} onClick={handleCheck} />
                <label for ='inCompleteList'> Complete </label>
                <br/>
            </form>
        </div>
    );
}
    
export default TodoList;
